mapper.select:
SELECT "eventName", "eventDate" AS "startDate", "eventDate" AS "endDate", 'Official holiday' as "eventType" FROM "officialHoliday" WHERE EXTRACT(month FROM TO_DATE("officialHoliday"."eventDate", 'DD/MM/YYYY')) = EXTRACT(month FROM CURRENT_DATE) AND EXTRACT(year FROM TO_DATE("officialHoliday"."eventDate", 'DD/MM/YYYY')) = EXTRACT(year FROM CURRENT_DATE)
UNION
SELECT "typeOfLeave"."typeName", "leaveRequest"."startDate", "leaveRequest"."endDate", 'Leave request' as "eventType" FROM "leaveRequest" INNER JOIN "typeOfLeave" ON "leaveRequest"."typeID" = "typeOfLeave"."typeID" INNER JOIN "leaveStatus" ON "leaveRequest"."leaveID" = "leaveStatus"."leaveID" WHERE "leaveRequest"."username" = #{username} AND EXTRACT(month FROM TO_DATE("leaveRequest"."startDate", 'DD/MM/YYYY')) = EXTRACT(month FROM CURRENT_DATE) AND EXTRACT(year FROM TO_DATE("leaveRequest"."startDate", 'DD/MM/YYYY')) = EXTRACT(year FROM CURRENT_DATE) AND ( "leaveStatus"."leaveStatus" != 'deleted' AND "leaveStatus"."leaveStatus" != 'rejected' ) AND NOT EXISTS ( SELECT 1 FROM "cancelLeave" WHERE "cancelLeave"."leaveID" = "leaveRequest"."leaveID" AND "cancelLeave"."status" = 'cancelled' ) ORDER BY "startDate";


			select "supervisor"
from "empRelation"
where "supervisor" = #{username}


			SELECT "firstname" || ' ' || "lastname" AS "fullname" 
FROM "user" 
WHERE "username" = #{supervisorUsername}


			select "eventName","eventDate" 
from "officialHoliday"
WHERE substring("eventDate",7,4) = #{year}
ORDER BY TO_DATE("eventDate", 'DD/MM/YYYY') ASC;

